mapper.update:
UPDATE ARORI_MEMBER SET
MEMBER_PW = #{member_pw} WHERE MEMBER_EMAIL =
#{member_email}

mapper.select:
SELECT member_email, member_phone,member_q, member_a FROM
ARORI_MEMBER where
member_no=#{member_no}

mapper.update:
UPDATE ARORI_MEMBER SET
MEMBER_PW=#{member_pw},
MEMBER_EMAIL=#{member_email},
MEMBER_PHONE=#{member_phone},
member_Q=#{member_Q},member_a=#{member_a}
WHERE MEMBER_NO=#{member_no}

mapper.select:
SELECT member_nick, member_state,member_join, member_login
FROM MEMBER where
member_no=#{member_no}

mapper.update:
UPDATE MEMBER SET
MEMBER_NICK= #{member_nick}
WHERE member_nick=#{member_nick}

mapper.select:
SELECT count
(*) FROM arori_member
WHERE member_pw=#{member_pw}

mapper.update:
